import { Injectable } from "@angular/core";
import { Company } from "./models";

const STORAGE_KEY = "companies_v1";

@Injectable({ providedIn: "root" })
export class CompanyService {
  private companies: Company[] = [];

  constructor() {
    const raw = localStorage.getItem(STORAGE_KEY);

    if (raw) {
      this.companies = JSON.parse(raw) as Company[];
    } else {
      // Seed data - use the exact property names from your Company model
      this.companies = [
        {
          id: "1",
          companyName: "Google",
          address: "1600 Amphitheatre Parkway",
          email: "contact@google.com",
          phoneNumber: "1234567890",
          createdAt: new Date().toISOString(),
          empInfo: [],
        },
        {
          id: "2",
          companyName: "Microsoft",
          address: "One Microsoft Way",
          email: "info@microsoft.com",
          phoneNumber: "9876543210",
          createdAt: new Date().toISOString(),
          empInfo: [],
        },
        {
          id: "3",
          companyName: "Amazon",
          address: "410 Terry Ave N",
          email: "support@amazon.com",
          phoneNumber: "5556667777",
          createdAt: new Date().toISOString(),
          empInfo: [],
        },
      ];
      this.persist();
    }
  }

  private persist() {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(this.companies));
  }

  list(): Company[] {
    return [...this.companies].sort((a, b) =>
      b.createdAt.localeCompare(a.createdAt)
    );
  }

  get(id: string): Company | undefined {
    return this.companies.find((c) => c.id === id);
  }

  create(company: Company) {
    // assume caller provides id/createdAt (your form already generates them);
    // if not, we can generate them here instead.
    this.companies.push(company);
    this.persist();
  }

  update(id: string, patch: Partial<Company>) {
    const idx = this.companies.findIndex((c) => c.id === id);
    if (idx >= 0) {
      this.companies[idx] = { ...this.companies[idx], ...patch };
      this.persist();
    }
  }

  delete(id: string) {
    this.companies = this.companies.filter((c) => c.id !== id);
    this.persist();
  }
}
