import { Injectable } from "@angular/core";
import { BehaviorSubject } from "rxjs";
import { Company } from "./models";

const STORAGE_KEY = "companies_v1";

@Injectable({ providedIn: "root" })
export class CompanyService {
  private companies: Company[] = [];
  private companies$ = new BehaviorSubject<Company[]>([]);

  constructor() {
    const raw = localStorage.getItem(STORAGE_KEY);
    this.companies = raw ? JSON.parse(raw) : [];
    this.companies$.next(this.list()); // emit initial list
  }

  private persist() {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(this.companies));
    this.companies$.next(this.list()); // emit updated list
  }

  list(): Company[] {
    return [...this.companies].sort((a, b) =>
      b.createdAt.localeCompare(a.createdAt)
    );
  }

  list$() {
    return this.companies$.asObservable();
  }

  get(id: string): Company | undefined {
    return this.companies.find((c) => c.id === id);
  }

  create(company: Company) {
    this.companies.push(company);
    this.persist();
  }

  update(id: string, company: Company) {
    const idx = this.companies.findIndex((c) => c.id === id);
    if (idx >= 0) {
      this.companies[idx] = company; // replace whole object
      this.persist();
    }
  }

  delete(id: string) {
    this.companies = this.companies.filter((c) => c.id !== id);
    this.persist();
  }
}
