import { Component } from "@angular/core";
import { Router } from "@angular/router";
import { CompanyService } from "../company.service";
import { Company } from "../models";

@Component({
  selector: "app-company-list",
  templateUrl: "./company-list.component.html",
  styleUrls: ["./company-list.component.css"],
})
export class CompanyListComponent {
  companies: Company[] = [];

  constructor(private svc: CompanyService, private router: Router) {
    this.companies = this.svc.list();
  }

  deleteCompany(c: Company) {
    const ok = confirm(`Delete "${c.companyName}"?`);
    if (ok) {
      this.svc.delete(c.id);
      this.companies = this.svc.list();
    }
  }
}
<div class="card">
  <div
    style="
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 0.5rem;
    "
  >
    <h3>Company List</h3>
    <a class="btn primary" routerLink="/new-company">‚ûï New Company</a>
  </div>

  <table class="table" *ngIf="companies.length; else empty">
    <thead>
      <tr>
        <th>Company Name</th>
        <th>Email</th>
        <th>Phone Number</th>
        <th>Created At</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let c of companies">
        <td>{{ c.companyName }}</td>
        <td>{{ c.email }}</td>
        <td>{{ c.phoneNumber }}</td>
        <td>{{ c.createdAt | date : "medium" }}</td>
        <td>
          <a
            class="icon-btn"
            [routerLink]="['/edit-company', c.id]"
            title="Edit"
            >‚úèÔ∏è</a
          >
          <button class="icon-btn" (click)="deleteCompany(c)" title="Delete">
            üóëÔ∏è
          </button>
        </td>
      </tr>
    </tbody>
  </table>

  <ng-template #empty>
    <div class="small">
      No companies yet. Click <b>New Company</b> to add one.
    </div>
  </ng-template>
</div>
