import { Component, OnInit } from "@angular/core";
import { CompanyService } from "../company.service";
import { Company } from "../models";

@Component({
  selector: "app-company-list",
  templateUrl: "./company-list.component.html",
  styleUrls: ["./company-list.component.css"],
})
export class CompanyListComponent implements OnInit {
  companies: Company[] = [];

  constructor(private svc: CompanyService) {}

  ngOnInit(): void {
    this.svc.list$().subscribe((list) => (this.companies = list));
  }

  deleteCompany(c: Company) {
    if (confirm(`Delete company "${c.companyName}"?`)) {
      this.svc.delete(c.id);
    }
  }
}
<div class="card">
  <div class="card-header">
    <h3>Company List</h3>
    <a class="btn primary" routerLink="/new-company">
      <span class="material-icons">add</span>
      New Company
    </a>
  </div>

  <div *ngIf="companies.length; else empty">
    <div *ngFor="let c of companies" class="company-card">
      <h4>{{ c.companyName }}</h4>
      <p>Email: {{ c.email }}</p>
      <p>Phone: {{ c.phoneNumber }}</p>
      <p>Created: {{ c.createdAt | date: "medium" }}</p>

      <div *ngIf="c.empInfo.length">
        <h5>Employees:</h5>
        <ul>
          <li *ngFor="let e of c.empInfo">
            {{ e.empName }} - {{ e.designation }} - {{ e.email }}
          </li>
        </ul>
      </div>

      <div class="actions">
        <a [routerLink]="['/edit-company', c.id]" class="icon-btn" title="Edit">
          <span class="material-icons">edit</span>
        </a>
        <button class="icon-btn danger" (click)="deleteCompany(c)" title="Delete">
          <span class="material-icons">delete</span>
        </button>
      </div>
    </div>
  </div>

  <ng-template #empty>
    <div class="small empty">
      <span class="material-icons">info</span>
      No companies yet. Click <b>New Company</b> to add one.
    </div>
  </ng-template>
</div>
